questions = ("What is the capital of France?: ",                       "What is the largest ocean on Earth?: ",                       "WWhich planet is known as the Red Planet?: ",                       "Who wrote “Romeo and Juliet”?: ",                       "Which planet in the solar system is the hottest?: ")options = (("A.Madrid", "B. Rome", "C. Paris", "D. Berlin"),                   ("A. Atlantic Ocean", "B. Indian Ocean", "C. Arctic Ocean", "D. Pacific Ocean"),                   ("A. Earth", "B. Mars", "C. Jupiter", "D. Saturn"),                   ("A. Charles Dickens", "B. J.K. Rowling", "C. William Shakespeare", "D. Mark Twain"),                   ("A. Mercury", "B. Venus", "C. Earth", "D. Mars"))answers = ("C", "D", "B", "C", "B")guesses = []score = 0question_num = 0for question in questions:    print("----------------------")    print(question)    for option in options[question_num]:        print(option)    guess = input("Enter (A, B, C, D): ").upper()    guesses.append(guess)    if guess == answers[question_num]:        score += 1        print("CORRECT!")    else:        print("INCORRECT!")        print(f"{answers[question_num]} is the correct answer")    question_num += 1print("----------------------")print("       RESULTS        ")print("----------------------")print("answers: ", end="")for answer in answers:    print(answer, end=" ")print()print("guesses: ", end="")for guess in guesses:    print(guess, end=" ")print()score = int(score / len(questions) * 100)print(f"Your score is: {score}%")